import{l as e,f as a,A as n}from"./framework.ab59a038.js";const r='{"title":"21B05","description":"","frontmatter":{"title":"21B05","subtitle":"quote and poem","category":"literal","date":"2021-01-19T00:00:00.000Z"},"headers":[{"level":3,"title":"An Essay on Man","slug":"an-essay-on-man"},{"level":3,"title":"Chinese Related","slug":"chinese-related"}],"relativePath":"daily/21B05.md","lastUpdated":1611200037755}',t={},s=n('<p><q>Be curious. Read widely. Try new things.</q></p><h5 id="aaron-swartz"><a class="header-anchor" href="#aaron-swartz" aria-hidden="true">#</a> Aaron Swartz</h5><h3 id="an-essay-on-man"><a class="header-anchor" href="#an-essay-on-man" aria-hidden="true">#</a> An Essay on Man</h3><p><q>All nature is but art, unknown to thee; All chance, direction, which thou canst not see; All discord, harmony not understood; All partial evil, universal good. And, spite of pride, in erring reason&#39;s spite, One truth is clear, &#39;Whatever is, is right.&#39; </q></p><h5 id="alexander-pope-an-essay-on-man"><a class="header-anchor" href="#alexander-pope-an-essay-on-man" aria-hidden="true">#</a> Alexander Pope, An Essay on Man</h5><hr><h3 id="chinese-related"><a class="header-anchor" href="#chinese-related" aria-hidden="true">#</a> Chinese Related</h3><p><q>命运是怎样地，在诗中一去永不复返 / 它是怎样地，在诗中成为模糊的影象？ / 所有发生过的事物，总是先于我们的判断 / 我们无从追赶，难以辨认 / 不要怕，如有逝者与你错过 / 同他们，平静地对视吧 / 无数人的忧伤，使你与众不同 / 我们目睹了，发生过的事物 / 那些时代的豪言壮语，并非为我们所说出 / 有何胜利可言？ / 挺住(忍耐)就是一切。</q></p><h5 id="里尔克-安魂曲"><a class="header-anchor" href="#里尔克-安魂曲" aria-hidden="true">#</a> 里尔克, 安魂曲</h5>',9);t.render=function(n,r,t,i,d,h){return e(),a("div",null,[s])};export default t;export{r as __pageData};
